    private void ConvertirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ConvertirActionPerformed
        // TODO add your handling code here:
        float tasa = Float.valueOf(tasaCambioIntroduceUsuario.getText());
        float divisaDollar = Float.valueOf(divisa1.getText());
        float divisaEuro = Float.valueOf(divisa2.getText());
        if(!dollarEuro){
            divisa1.setText(String.valueOf(divisaEuro/tasa));
        }else{
            divisa2.setText(String.valueOf(tasa*divisaDollar));
        }
        
        
    }//GEN-LAST:event_ConvertirActionPerformed

