    private void CambiarsentidoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CambiarsentidoActionPerformed
        // TODO add your handling code here:
        if(dollarEuro){
            dollarEuro = false;
            Símbolo.setText("$ <-- €");
        }else{
            dollarEuro = true;
            Símbolo.setText("$ --> €");
        }
        
    }//GEN-LAST:event_CambiarsentidoActionPerformed

